            searchAdapter.setAlreadyAdded(alreadyAddedNames);
        }

        // ✅ Optional: Clear search after adding exercise
        clearSearch();
    }

    // ✅ Save all changes to Firestore
    private void saveWorkoutToFirestore() {
        List<Map<String, Object>> exerciseList = new ArrayList<>();

        for (WorkoutExercise we : workoutExercises) {
            Map<String, Object> map = new HashMap<>();
            Map<String, Object> info = new HashMap<>();
            if (we.getExerciseInfo() != null) {
                info.put("name", we.getExerciseInfo().getName());
                info.put("gifUrl", we.getExerciseInfo().getGifUrl());
            }
            map.put("exerciseInfo", info);
            map.put("sets", we.getSets());
            map.put("reps", we.getReps());
            map.put("restSeconds", we.getRestSeconds());

            exerciseList.add(map);
        }

        workoutRef.update("exercises", exerciseList)
                .addOnSuccessListener(aVoid -> {
                    Toast.makeText(this, "Workout saved!", Toast.LENGTH_SHORT).show();
                    hasChanges = false;
                    saveButton.setVisibility(View.GONE); //  hide button again after saving
                })
                .addOnFailureListener(e ->
                        Toast.makeText(this, "Save failed: " + e.getMessage(), Toast.LENGTH_SHORT).show());
    }

    // Load PT Sessions from membership
    private void loadPTSessions(FirebaseFirestore db) {
        db.collection("memberships")
                .document(clientUid)
                .addSnapshotListener((documentSnapshot, error) -> {
                    if (error != null) {
                        Log.e(TAG, "Error loading PT sessions", error);
                        return;
                    }

                    if (documentSnapshot != null && documentSnapshot.exists()) {
                        Long sessions = documentSnapshot.getLong("sessions");
                        currentSessions = sessions != null ? sessions.intValue() : 0;
                        clientRemainingSessions.setText(String.valueOf(currentSessions));

                        // Disable button if no sessions remaining
                        if (currentSessions <= 0) {
                            markSessionCompleteButton.setEnabled(false);
                            markSessionCompleteButton.setAlpha(0.5f);
                        } else {
                            markSessionCompleteButton.setEnabled(true);
                            markSessionCompleteButton.setAlpha(1.0f);
                        }
                    } else {
                        currentSessions = 0;
                        clientRemainingSessions.setText("0");
                        markSessionCompleteButton.setEnabled(false);
                        markSessionCompleteButton.setAlpha(0.5f);
                    }
                });
    }

    // Mark session as complete and decrease count
    private void markSessionComplete(FirebaseFirestore db) {
        if (currentSessions <= 0) {
            Toast.makeText(this, "No sessions remaining!", Toast.LENGTH_SHORT).show();
            return;
        }

        // Show confirmation dialog
        new androidx.appcompat.app.AlertDialog.Builder(this)
                .setTitle("Mark Session Complete")
                .setMessage("Confirm that you completed a session with this client? This will reduce their remaining sessions by 1.")
                .setPositiveButton("Confirm", (dialog, which) -> {
                    // Decrease sessions in membership document
                    int newSessionCount = currentSessions - 1;

                    Map<String, Object> updates = new HashMap<>();
                    updates.put("sessions", newSessionCount);

                    db.collection("memberships")
                            .document(clientUid)
                            .update(updates)
                            .addOnSuccessListener(aVoid -> {
                                Toast.makeText(this, "Session marked complete! Remaining: " + newSessionCount, Toast.LENGTH_SHORT).show();
                                Log.d(TAG, "Session completed. New count: " + newSessionCount);
                            })
                            .addOnFailureListener(e -> {
                                Toast.makeText(this, "Failed to update sessions: " + e.getMessage(), Toast.LENGTH_SHORT).show();
                                Log.e(TAG, "Error updating sessions", e);
                            });
                })
                .setNegativeButton("Cancel", null)
                .show();
    }

    private void onWorkoutChanged() {
        if (!hasChanges) {
            hasChanges = true;
            saveButton.setVisibility(View.VISIBLE); // show the save button
        }
    }

    // Hide soft keyboard
    private void hideKeyboard() {
        View view = getCurrentFocus();
        if (view == null) {
            view = new View(this);
        }
        android.view.inputmethod.InputMethodManager imm =
            (android.view.inputmethod.InputMethodManager) getSystemService(android.content.Context.INPUT_METHOD_SERVICE);
        if (imm != null) {
            imm.hideSoftInputFromWindow(view.getWindowToken(), 0);
        }
    }
}

package com.example.signuploginrealtime;

import android.app.Activity;
import android.content.Context;
import android.content.Intent;
import android.util.Log;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;
import com.bumptech.glide.Glide;
import android.widget.ImageView;

import androidx.annotation.NonNull;
import androidx.cardview.widget.CardView;
import androidx.recyclerview.widget.RecyclerView;

import java.util.List;

public class ClientsAdapter extends RecyclerView.Adapter<ClientsAdapter.ClientViewHolder> {

    private Context context;
    private List<coach_clients.Client> clientsList;
    private OnClientLongClickListener longClickListener;

    public interface OnClientLongClickListener {
        void onClientLongClick(coach_clients.Client client);
    }

    public ClientsAdapter(Context context, List<coach_clients.Client> clientsList) {
        this.context = context;
        this.clientsList = clientsList;
    }

    public ClientsAdapter(Context context, List<coach_clients.Client> clientsList, OnClientLongClickListener longClickListener) {
        this.context = context;
        this.clientsList = clientsList;
        this.longClickListener = longClickListener;
    }

    @NonNull
    @Override
    public ClientViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(context).inflate(R.layout.item_client, parent, false);
        return new ClientViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull ClientViewHolder holder, int position) {
        coach_clients.Client client = clientsList.get(position);

        holder.clientName.setText(client.getName());
        holder.clientEmail.setText(client.getEmail());
        holder.clientStatus.setText(client.getStatus());

        // Display PT sessions
        int sessions = client.getSessions();
        if (sessions > 0) {
            holder.clientSessions.setText(sessions + " PT Sessions");
            holder.clientSessions.setVisibility(View.VISIBLE);
        } else {
            holder.clientSessions.setText("No PT Sessions");
            holder.clientSessions.setVisibility(View.VISIBLE);
            holder.clientSessions.setVisibility(View.VISIBLE);
        }

        String profilePictureUrl = client.getProfilePictureUrl();

        if (profilePictureUrl != null && !profilePictureUrl.isEmpty()) {
            holder.clientProfileImage.setVisibility(View.VISIBLE);
            holder.clientAvatar.setVisibility(View.GONE);

            Glide.with(context)
                    .load(profilePictureUrl)
                    .circleCrop()
                    .placeholder(R.drawable.ic_profile)
                    .error(R.drawable.ic_profile)
                    .into(holder.clientProfileImage);
        } else {
            holder.clientProfileImage.setVisibility(View.GONE);
            holder.clientAvatar.setVisibility(View.VISIBLE);

            if (client.getName() != null && !client.getName().isEmpty()) {
                holder.clientAvatar.setText(String.valueOf(client.getName().charAt(0)).toUpperCase());
            }
        }

        // ✅ SIMPLIFIED: Only "Active" status exists (green badge)
        String status = client.getStatus();
        if (status != null && status.equals("Active")) {
            holder.clientStatus.setBackgroundResource(R.drawable.status_active);
        } else {
            // Fallback (shouldn't happen since all clients with membership are active)
            holder.clientStatus.setBackgroundResource(R.drawable.status_unknown);
        }

        holder.clientCard.setOnClickListener(v -> {
            Intent intent = new Intent(context, Client_workouts_details.class);
            intent.putExtra("client_uid", client.getUid());
            if (!(context instanceof Activity)) {
                intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK);
            }
            context.startActivity(intent);
        });

        holder.clientCard.setOnLongClickListener(v -> {
            if (longClickListener != null) {
                longClickListener.onClientLongClick(client);
                return true;
            }
            return false;
        });
    }

    @Override
    public int getItemCount() {
        return clientsList.size();
    }

    public static class ClientViewHolder extends RecyclerView.ViewHolder {
        CardView clientCard;
        TextView clientName, clientEmail, clientStatus, clientAvatar, clientSessions;
        ImageView clientProfileImage;

        public ClientViewHolder(@NonNull View itemView) {
            super(itemView);
            clientCard = itemView.findViewById(R.id.client_card);
            clientName = itemView.findViewById(R.id.client_name);
            clientEmail = itemView.findViewById(R.id.client_email);
            clientStatus = itemView.findViewById(R.id.client_status);
            clientAvatar = itemView.findViewById(R.id.client_avatar);
            clientProfileImage = itemView.findViewById(R.id.client_profile_image);
            clientSessions = itemView.findViewById(R.id.client_sessions);
        }
    }
}

